const accordionHeaders=document.getElementsByClassName("league__header"),tabsBtnList=document.querySelectorAll(".tabs__tab"),tabs=document.querySelectorAll(".tabsContent__item");for(let t=0;t<accordionHeaders.length;t++)accordionHeaders[t].addEventListener("click",(function(){this.classList.toggle("active")}));for(let t=0;t<tabsBtnList.length;t++)tabsBtnList[t].addEventListener("click",(e=>{for(let t=0;t<tabsBtnList.length;t++)tabsBtnList[t].classList.remove("active");e.currentTarget.classList.add("active");for(let t=0;t<tabs.length;t++)tabs[t].classList.remove("active");document.getElementById(`tab${t+1}`).classList.add("active")}));